//var input = "^v";

var santa = [];
var robosanta = [];

//var input = "><><";
var path = [];
var unique = [];

function parse(s){
    return s.split("");
}

function roboParse(s) {

    s.split("");

    for( var i = 0; i < s.length; i++) {
        if( i % 2 == 0) {
            santa.push(s[i]);
        } else {
            robosanta.push(s[i]);
        }
    }

}

function move(d,current) {

    var dest = {
        x: current.x,
        y: current.y
    };

    switch (d) {
        case "^":
            dest.x = dest.x - 1;
            break;
        case "v":
            dest.x = dest.x + 1;
            break;
        case ">":
            dest.y = dest.y + 1;
            break;
        case "<":
            dest.y = dest.y - 1;
            break;
    }

    return dest;

}

function addToPath(current) {
    path.push(current);
}

function addToUnique(current) {
    unique.push(current);
}

function isNewHouse(current) {

    var i;
    for (i in unique) {
        if (unique.hasOwnProperty(i) && unique[i].x === current.x && unique[i].y === current.y) {
            return false;
        }
    }

    return true;
}

function deliveryPath(input) {

    var delivery = parse(input);

    var curr = {};
    var start = {
        x: 0,
        y: 0
    };
    addToPath(start);
    addToUnique(start);

    for (var i = 0; i < delivery.length; i++ ) {

        if( i == 0 ) {
          var next = move(delivery[i],start);
          var curr = {
              x: next.x,
              y: next.y
          };
          addToPath(next);
            if( isNewHouse(next)){
                addToUnique(next);
            }
        } else {
            var curr = move(delivery[i],curr);
            addToPath(curr);
            if( isNewHouse(curr)){
                addToUnique(curr);
            }
        }
    }
}


function deliveryRoboPath(delivery) {

    var curr = {};
    var start = {
        x: 0,
        y: 0
    };
    addToPath(start);
    addToUnique(start);

    for (var i = 0; i < delivery.length; i++ ) {

        if( i == 0 ) {
            var next = move(delivery[i],start);
            var curr = {
                x: next.x,
                y: next.y
            };
            addToPath(next);
            if( isNewHouse(next)){
                addToUnique(next);
            }
        } else {
            var curr = move(delivery[i],curr);
            addToPath(curr);
            if( isNewHouse(curr)){
                addToUnique(curr);
            }
        }
    }
}

function countHouses(p) {

    return p.length;
}


